import styled from "styled-components";

export const Container = styled.div`
  position: absolute;
  width: 100vw;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  form {
    display: flex;
    flex-direction: column;

    input {
      padding: 10px;
      margin: 5px;
      border-radius: 6px;
      border: 1px solid black;
    }

    .submitButton {
      border: none;
      cursor: pointer;
    }
  }
`;
