import React, { useEffect, useState } from "react";
import { Container } from "./styles";

function Main() {
  const [username, setUsername] = useState();
  const [html, setHTML] = useState("");

  useEffect(() => {
    console.log(username);
  }, [username]);

  const initIns = (e) => {
    fetch(
      `${process.env.REACT_APP_URL}/instance/init?token=${process.env.REACT_APP_SECRET_TOKEN}&key=${username}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      }
    ).then(async (res) => {
      switch (res.status) {
        case 200:
          let data = await res.json();

          alert(`Usuário criado com sucesso, por favor leia o QR code!! + ${data.key}`);

          fetch(`${process.env.REACT_APP_URL}/instance/qr?key=${username}`, {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
          }).then(async (res) => {
            return res.text()
          }).then(function (text) {
            let parser = DOMParser();

            let doc = parser.parseFromString(text, "text/html")


            console.log(text)
            setHTML(text)
          });
          break;
      }
    });

    e.preventDefault();
  };

  return (
    <Container>
      <form onSubmit={initIns}>
        <input
          type="text"
          name="username"
          onChange={(e) => setUsername(e.target.value)}
        />
        <input type="submit" />
      </form>
      <div dangerouslySetInnerHTML={{__html: `${html}`}} />
    </Container>
  );
}

export default Main;
