import React, { useEffect, useState } from "react";
import { Container } from "./styles";

function Main() {
  const [username, setUsername] = useState();
  const [html, setHTML] = useState("");

  useEffect(() => {
    console.log(username);
  }, [username]);

  const initIns = (e) => {
    fetch(
      `${process.env.REACT_APP_URL}/instance/init?token=${process.env.REACT_APP_SECRET_TOKEN}&key=${username}`,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      }
    ).then((res) => res.json()).then((json) => {
      console.log(json)
      fetch(`${process.env.REACT_APP_URL}/instance/qr?key=${username}`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
        .then(async (res) => {
          return res.text();
        })
        .then(function (text) {
          let parser = new DOMParser();
  
          var doc = parser.parseFromString(text, "text/html");
          let qrcode = doc.getElementById("qrcode_box").src;
  
          console.log(qrcode);
          setHTML(qrcode);
        });
    })

   

    e.preventDefault();
  }

  return (
    <Container>
      <form onSubmit={initIns}>
        <input
          type="text"
          name="username"
          onChange={(e) => setUsername(e.target.value)}
        />
        <input type="submit" />
      </form>
      <img src={html} />
    </Container>
  );
}

export default Main;
