import React, { useEffect, useState } from "react";
import { Container } from "./styles";

function Main() {
  const [username, setUsername] = useState();
  const [userInit, setUserInit] = useState(false);
  const [html, setHTML] = useState("");

  useEffect(() => {
    console.log(username);
  }, [username]);

  const QrCodeGen = (e) => {
    fetch(`${process.env.REACT_APP_URL}/instance/qr?key=${username}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then(async (res) => {
        switch (res.status) {
          case 200:
            return res.text();
            break;
        }
      })
      .then(function (text) {
        let parser = new DOMParser();

        var doc = parser.parseFromString(text, "text/html");
        let qrcode = doc.getElementById("qrcode_box").src;

        console.log(qrcode);
        setHTML(qrcode);
      });
  };

  const initIns = (e) => {
    if (!userInit) {
      fetch(
        `${process.env.REACT_APP_URL}/instance/init?token=${process.env.REACT_APP_SECRET_TOKEN}&key=${username}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      ).then(async (res) => {
        switch (res.status) {
          case 200:
            QrCodeGen();
            setUserInit(true);
            break;
          case 404:
            alert(`servidor fora do ar - ${res.status} - ${res.statusText}`);
            break;
        }
      });
    } else {
      QrCodeGen();
    }

    e.preventDefault();
  };

  return (
    <Container>
      <form onSubmit={initIns}>
        <input
          type="text"
          name="username"
          onChange={(e) => setUsername(e.target.value)}
        />
        <input type="submit" />
      </form>
      <img src={html} />
    </Container>
  );
}

export default Main;
